                e.preventDefault();
                var product = $('.search_input').val();

                var data = {
                    product   : product,
                }

                $.ajax({
                    url:  '{{route('search.product',$slug)}}',
                    context: this,
                    data: data,
                    success: function(responce) {
                        window.location.href =responce;
                    }
                });
            });
        });
